INSERT INTO `wp_wc_admin_notes` (`note_id`, `name`, `type`, `locale`, `title`, `content`, `content_data`, `status`, `source`, `date_created`, `date_reminder`, `is_snoozable`, `layout`, `image`, `is_deleted`, `icon`) VALUES
(1, 'paypal_ppcp_gtm_2021', 'marketing', 'en_US', 'Offer more options with the new PayPal', 'Get the latest PayPal extension for a full suite of payment methods with extensive currency and country coverage.', '{}', 'pending', 'woocommerce.com', '2021-05-11 15:58:56', NULL, 0, 'plain', '', 0, 'info'),
(2, 'facebook_pixel_api_2021', 'marketing', 'en_US', 'Improve the performance of your Facebook ads', 'Enable Facebook Pixel and Conversions API through the latest version of Facebook for WooCommerce for improved measurement and ad targeting capabilities.', '{}', 'pending', 'woocommerce.com', '2021-05-11 15:58:56', NULL, 0, 'plain', '', 0, 'info'),
(3, 'facebook_ec_2021', 'marketing', 'en_US', 'Sync your product catalog with Facebook to help boost sales', 'A single click adds all products to your Facebook Business Page shop. Product changes are automatically synced, with the flexibility to control which products are listed.', '{}', 'pending', 'woocommerce.com', '2021-05-11 15:58:56', NULL, 0, 'plain', '', 0, 'info'),
(4, 'ecomm-need-help-setting-up-your-store', 'info', 'en_US', 'Need help setting up your Store?', 'Schedule a free 30-min <a href=\"https://wordpress.com/support/concierge-support/\">quick start session</a> and get help from our specialists. We’re happy to walk through setup steps, show you around the WordPress.com dashboard, troubleshoot any issues you may have, and help you the find the features you need to accomplish your goals for your site.', '{}', 'pending', 'woocommerce.com', '2021-05-11 15:58:56', NULL, 0, 'plain', '', 0, 'info'),
(5, 'woocommerce-services', 'info', 'en_US', 'WooCommerce Shipping & Tax', 'WooCommerce Shipping &amp; Tax helps get your store “ready to sell” as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.', '{}', 'unactioned', 'woocommerce.com', '2021-05-11 22:02:38', NULL, 0, 'plain', '', 0, 'info'),
(6, 'ecomm-unique-shopping-experience', 'info', 'en_US', 'For a shopping experience as unique as your customers', 'Product Add-Ons allow your customers to personalize products while they’re shopping on your online store. No more follow-up email requests—customers get what they want, before they’re done checking out. Learn more about this extension that comes included in your plan.', '{}', 'pending', 'woocommerce.com', '2021-05-11 15:58:56', NULL, 0, 'plain', '', 0, 'info'),
(7, 'wc-admin-getting-started-in-ecommerce', 'info', 'en_US', 'Getting Started in eCommerce - webinar', 'We want to make eCommerce and this process of getting started as easy as possible for you. Watch this webinar to get tips on how to have our store up and running in a breeze.', '{}', 'pending', 'woocommerce.com', '2021-05-11 15:58:56', NULL, 0, 'plain', '', 0, 'info'),
(8, 'your-first-product', 'info', 'en_US', 'Your first product', 'That\'s huge! You\'re well on your way to building a successful online store — now it’s time to think about how you\'ll fulfill your orders.<br /><br />Read our shipping guide to learn best practices and options for putting together your shipping strategy. And for WooCommerce stores in the United States, you can print discounted shipping labels via USPS with <a href=\"https://href.li/?https://woocommerce.com/shipping\" target=\"_blank\">WooCommerce Shipping</a>.', '{}', 'unactioned', 'woocommerce.com', '2021-05-13 07:51:08', NULL, 0, 'plain', '', 0, 'info'),
(9, 'wc-square-apple-pay-boost-sales', 'marketing', 'en_US', 'Boost sales with Apple Pay', 'Now that you accept Apple Pay® with Square you can increase conversion rates by letting your customers know that Apple Pay® is available. Here’s a marketing guide to help you get started.', '{}', 'pending', 'woocommerce.com', '2021-05-11 15:58:56', NULL, 0, 'plain', '', 0, 'info'),
(10, 'wc-square-apple-pay-grow-your-business', 'marketing', 'en_US', 'Grow your business with Square and Apple Pay ', 'Now more than ever, shoppers want a fast, simple, and secure online checkout experience. Increase conversion rates by letting your customers know that you now accept Apple Pay®.', '{}', 'pending', 'woocommerce.com', '2021-05-11 15:58:56', NULL, 0, 'plain', '', 0, 'info'),
(11, 'wcpay-apple-pay-is-now-available', 'marketing', 'en_US', 'Apple Pay is now available with WooCommerce Payments!', 'Increase your conversion rate by offering a fast and secure checkout with <a href=\"https://woocommerce.com/apple-pay/?utm_source=inbox&amp;utm_medium=product&amp;utm_campaign=wcpay_applepay\" target=\"_blank\">Apple Pay</a>®. It’s free to get started with <a href=\"https://woocommerce.com/payments/?utm_source=inbox&amp;utm_medium=product&amp;utm_campaign=wcpay_applepay\" target=\"_blank\">WooCommerce Payments</a>.', '{}', 'pending', 'woocommerce.com', '2021-05-11 15:58:56', NULL, 0, 'plain', '', 0, 'info'),
(12, 'wcpay-apple-pay-boost-sales', 'marketing', 'en_US', 'Boost sales with Apple Pay', 'Now that you accept Apple Pay® with WooCommerce Payments you can increase conversion rates by letting your customers know that Apple Pay® is available. Here’s a marketing guide to help you get started.', '{}', 'pending', 'woocommerce.com', '2021-05-11 15:58:56', NULL, 0, 'plain', '', 0, 'info'),
(13, 'wcpay-apple-pay-grow-your-business', 'marketing', 'en_US', 'Grow your business with WooCommerce Payments and Apple Pay', 'Now more than ever, shoppers want a fast, simple, and secure online checkout experience. Increase conversion rates by letting your customers know that you now accept Apple Pay®.', '{}', 'pending', 'woocommerce.com', '2021-05-11 15:58:56', NULL, 0, 'plain', '', 0, 'info'),
(14, 'wc-admin-optimizing-the-checkout-flow', 'info', 'en_US', 'Optimizing the checkout flow', 'It\'s crucial to get your store\'s checkout as smooth as possible to avoid losing sales. Let\'s take a look at how you can optimize the checkout experience for your shoppers.', '{}', 'pending', 'woocommerce.com', '2021-05-11 15:58:56', NULL, 0, 'plain', '', 0, 'info'),
(15, 'wc-admin-first-five-things-to-customize', 'info', 'en_US', 'The first 5 things to customize in your store', 'Deciding what to start with first is tricky. To help you properly prioritize, we\'ve put together this short list of the first few things you should customize in WooCommerce.', '{}', 'unactioned', 'woocommerce.com', '2021-06-14 12:47:11', NULL, 0, 'plain', '', 0, 'info'),
(16, 'wc-payments-qualitative-feedback', 'info', 'en_US', 'WooCommerce Payments setup - let us know what you think', 'Congrats on enabling WooCommerce Payments for your store. Please share your feedback in this 2 minute survey to help us improve the setup process.', '{}', 'pending', 'woocommerce.com', '2021-05-11 15:58:56', NULL, 0, 'plain', '', 0, 'info'),
(17, 'share-your-feedback-on-paypal', 'info', 'en_US', 'Share your feedback on PayPal', 'Share your feedback in this 2 minute survey about how we can make the process of accepting payments more useful for your store.', '{}', 'pending', 'woocommerce.com', '2021-05-11 15:58:56', NULL, 0, 'plain', '', 0, 'info'),
(18, 'wcpay_instant_deposits_gtm_2021', 'marketing', 'en_US', 'Get paid within minutes – Instant Deposits for WooCommerce Payments', 'Stay flexible with immediate access to your funds when you need them – including nights, weekends, and holidays. With <a href=\"https://woocommerce.com/products/woocommerce-payments/?utm_source=inbox&amp;utm_medium=product&amp;utm_campaign=wcpay_instant_deposits\">WooCommerce Payments\'</a> new Instant Deposits feature, you’re able to transfer your earnings to a debit card within minutes.', '{}', 'pending', 'woocommerce.com', '2021-05-11 15:58:56', NULL, 0, 'plain', '', 0, 'info'),
(19, 'wc-admin-onboarding-email-marketing', 'info', 'en_US', 'Inscrivez-vous pour recevoir des astuces, des mises à jour des produits et des sources d’inspiration', 'Nous sommes là pour vous, obtenir des astuces, mises à jour de produits et inspirations directement dans votre boîte e-mail', '{}', 'unactioned', 'woocommerce-admin', '2021-05-17 07:58:57', NULL, 0, 'plain', '', 0, 'info'),
(20, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Se connecter à WooCommerce.com', 'Connectez-vous pour obtenir des notifications et des mises à jour importantes sur les produits.', '{}', 'unactioned', 'woocommerce-admin', '2021-05-17 07:58:57', NULL, 0, 'plain', '', 0, 'info'),
(21, 'wc-admin-selling-online-courses', 'marketing', 'en_US', 'Voulez-vous vendre des cours en ligne ?', 'Les cours en ligne sont une excellente solution pour toute entreprise qui peut enseigner une nouvelle compétence. Comme les cours ne nécessitent pas de développement de produits physiques ni d’expédition, ils sont abordables, rapides à créer et peuvent générer des revenus passifs pour les années à venir. Dans cet article, nous vous fournissons davantage d’informations sur la vente de cours à l’aide de WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2021-05-17 08:00:18', NULL, 0, 'plain', '', 0, 'info'),
(22, 'wc-admin-learn-more-about-variable-products', 'info', 'en_US', 'En savoir plus sur les produits variables', 'Les produits variables sont un type de produit puissant qui vous permet de proposer un ensemble de variantes d’un produit avec contrôle des prix, du stock, de l’image et plus pour chaque variante. Vous pouvez l’appliquer à un produit comme une chemise, disponible en plusieurs tailles et dans différentes couleurs.', '{}', 'unactioned', 'woocommerce-admin', '2021-05-18 07:51:08', NULL, 0, 'plain', '', 0, 'info'),
(23, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Vous cherchez un thème ?', 'Découvrez les thèmes compatibles avec WooCommerce et choisissez celui qui correspond le mieux à l’image de votre marque et à vos besoins.', '{}', 'unactioned', 'woocommerce-admin', '2021-05-18 07:59:47', NULL, 0, 'plain', '', 0, 'info'),
(24, 'wc-admin-insight-first-product-and-payment', 'survey', 'en_US', 'Tendances', 'Plus de 80 % des nouveaux commerçants ajoutent leur premier produit et configurent au moins un moyen de paiement durant la première semaine.<br><br>Trouvez-vous ce type d\'information utile ?', '{}', 'unactioned', 'woocommerce-admin', '2021-05-18 07:59:47', NULL, 0, 'plain', '', 0, 'info'),
(25, 'storefront-customize', 'info', 'en_US', 'Concevez votre boutique avec Storefront 🎨', 'Visitez la page des réglages pour démarrer la configuration et la personnalisation de votre boutique.', '{}', 'unactioned', 'storefront', '2021-05-18 13:29:59', NULL, 0, 'plain', '', 0, 'info'),
(26, 'wc-admin-mobile-app', 'info', 'en_US', 'Installer l’application mobile Woo', 'Installez l’application mobile WooCommerce pour gérer les commandes, recevoir des notifications de vente et afficher les statistiques principales où que vous soyez.', '{}', 'unactioned', 'woocommerce-admin', '2021-05-19 08:01:36', NULL, 0, 'plain', '', 0, 'info'),
(27, 'wc-admin-filter-by-product-variations-in-reports', 'info', 'en_US', 'Nouveau - filtrer les variations de produit dans les commandes et rapports de produits', 'L’une des fonctionnalités les plus attendues est enfin disponible ! Vous disposez désormais des tendances pour chaque variante de produit dans les commandes et les rapports sur les produits.', '{}', 'unactioned', 'woocommerce-admin', '2021-05-19 08:01:37', NULL, 0, 'banner', 'http://localhost/cartepvc/wp-content/plugins/woocommerce/packages/woocommerce-admin/images/admin_notes/filter-by-product-variations-note.svg', 0, 'info'),
(28, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'Comment personnaliser votre catalogue de produits', 'Vous souhaitez soigner l’apparence de vos images et de votre catalogue de produits tout en vous conformant à votre marque. Ce guide contient tous les conseils dont vous avez besoin pour mettre en valeur vos produits dans votre boutique.', '{}', 'unactioned', 'woocommerce-admin', '2021-05-19 08:01:38', NULL, 0, 'plain', '', 0, 'info'),
(29, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', 'Commencer à accepter les paiements sur votre boutique !', 'Accepter les paiements avec le fournisseur qui vous convient, parmi plus de 100 passerelles de paiement pour WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2021-05-22 19:25:53', NULL, 0, 'plain', '', 0, 'info'),
(30, 'wc-admin-marketing-intro', 'info', 'en_US', 'Connectez-vous à votre audience', 'Faites croître votre base clients et augmentez vos ventes avec les outils marketing créés pour WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2021-05-22 19:25:53', NULL, 0, 'plain', '', 0, 'info'),
(31, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Aider WooCommerce à améliorer ses services grâce au suivi de l’utilisation', 'Récupérer les données d’utilisation nous permet d’améliorer WooCommerce. Votre boutique sera prise en compte lors de l’évaluation des nouvelles fonctionnalités et de la qualité d’une mise à jour, ou pour déterminer l’intérêt d’une amélioration. Vous pouvez toujours consulter les <a href=\"http://localhost/cartepvc/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Paramètres</a> et choisir d’interrompre le partage des données. <a href=\"https://woocommerce.com/usage-tracking\" target=\"_blank\">En savoir plus</a> sur les données que nous collectons.', '{}', 'unactioned', 'woocommerce-admin', '2021-05-24 18:37:30', NULL, 0, 'plain', '', 0, 'info'),
(32, 'wc-admin-store-notice-giving-feedback-2', 'info', 'en_US', 'Nous vous invitons à partager votre expérience', 'Maintenant que vous nous avez choisis comme partenaire, notre objectif est de nous assurer que nous vous fournissons les bons outils pour répondre à vos besoins. Nous sommes impatients de recevoir vos commentaires sur votre expérience de conception de la boutique pour que nous puissions l’améliorer à l’avenir.', '{}', 'unactioned', 'woocommerce-admin', '2021-05-25 07:59:23', NULL, 0, 'plain', '', 0, 'info'),
(33, 'wc-admin-insight-first-sale', 'survey', 'en_US', 'Le saviez-vous ?', 'Une boutique optimisée par WooCommerce nécessite en moyenne 31 jours pour réaliser sa première vente. Vous êtes sur la bonne voie ! Trouvez-vous ce type d’information utile ?', '{}', 'unactioned', 'woocommerce-admin', '2021-05-25 07:59:23', NULL, 0, 'plain', '', 0, 'info'),
(34, 'wc-subscriptions-security-update-3-0-15', 'info', 'en_US', 'WooCommerce Subscriptions security update!', 'We recently released an important security update to WooCommerce Subscriptions. To ensure your site\'s data is protected, please upgrade <strong>WooCommerce Subscriptions to version 3.0.15</strong> or later.<br /><br />Click the button below to view and update to the latest Subscriptions version, or log in to <a href=\"https://woocommerce.com/my-dashboard\">WooCommerce.com Dashboard</a> and navigate to your <strong>Downloads</strong> page.<br /><br />We recommend always using the latest version of WooCommerce Subscriptions, and other software running on your site, to ensure maximum security.<br /><br />If you have any questions we are here to help — just <a href=\"https://woocommerce.com/my-account/create-a-ticket/\">open a ticket</a>.', '{}', 'pending', 'woocommerce.com', '2021-05-28 21:25:24', NULL, 0, 'plain', '', 0, 'info'),
(35, 'google_listings_and_ads_install', 'marketing', 'en_US', 'Drive traffic and sales with Google', 'Reach online shoppers to drive traffic and sales for your store by showcasing products across Google, for free or with ads.', '{}', 'pending', 'woocommerce.com', '2021-06-06 17:10:27', NULL, 0, 'plain', '', 0, 'info'),
(36, 'woocommerce-core-update-5-4-0', 'info', 'en_US', 'Update to WooCommerce 5.4.1 now', 'WooCommerce 5.4.1 addresses a checkout issue discovered in WooCommerce 5.4. We recommend upgrading to WooCommerce 5.4.1 as soon as possible.', '{}', 'pending', 'woocommerce.com', '2021-06-13 09:19:51', NULL, 0, 'plain', '', 0, 'info'),
